var MyExampleGetEmail = Class.create();
MyExampleGetEmail.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getEmail: function() {
    var userRecord = new GlideRecord("sys_user");
    //userRecord.get(this.getParameter('sysparm_userid'));
    //return userRecord.email + '';
		//var userdetails ={}
	userRecord.addQuery('sys_id',this.getParameter('sysparm_userid'));
		userRecord.query();
		if(userRecord.next()){
			return userRecord.email+'';
		}

  },

    type: 'MyExampleGetEmail'
});
